import React, { Component } from 'react';
import {BrowserRouter, Route, Switch, Redirect } from 'react-router-dom';
import Dashboard from "./Dashboard";
import LoginForm from './Component/auth/LoginForm';
import RegisterForm from './Component/auth/RegisterForm';
import { createStore } from 'redux';
import appReducer from './reducers/index';
import {Provider} from 'react-redux';
import { loadUser } from './actions/authActions';
import { Store, ExitToApp } from '@material-ui/icons';
import store from './store';

import { connect } from 'react-redux';
import PropTypes from 'prop-types';

import './App.css';
import Landing from './Landing';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {auth:PropTypes.object.isRequired};
  }

  componentDidMount() {
    store.dispatch(loadUser());
  }


  static propTypes = {
    auth: PropTypes.object.isRequired
  };


  render() {
    const { isAuthenticated, user } = this.props.auth;
    return (
      <BrowserRouter>
          <Switch>
              <PrivateRoute  exact path="/" isLoggedIn={ isAuthenticated } component={Dashboard} />

              {/* <Route path="/register" component={ RegisterForm}/> */}
              <Route path="/login" component={ LoginForm} />
              <Route path="*" component={() => 
                <h1>
                  404 NOT FOUND
                  </h1>} />
            </Switch>  
      </BrowserRouter>


    );
  }
}




const PrivateRoute = ({ component: Component,isLoggedIn, ...props }) => 
{
  return (
    <Route
      {...props}
      render = {(props) => isLoggedIn
        ? <Component {...props} />
        : <Redirect to={{pathname: '/login', state: {from: props.location}}} />}
    />
  )
}


const mapStateToProps = state => ({
  auth: state.auth
});


export default connect(
  mapStateToProps,
  null
)(App);
