import axios from 'axios';
import store from '../store'
 import { DATE_SELECT, SALT_RETURNS} from './types';
 // Date is select
 export const dateSelect = (start , end) => {
   
     return {
                 type: DATE_SELECT,
                 payload:{start,end}
              };
     // console.log("start ",start);
     // console.log("end ",end);
     
 }
 
 // Clear date
 export const saltReturns =  () => (dispatch, getState) => {
     let tempRes=[];
     const start=store.getState().date.start;
     const end=store.getState().date.end;
     let index=start;
     console.log("000000000000000000000000000000000000000000000000");
     for (let index = start; index < end; index.setDate(index.getDate() + 1)) 
     {
        
        let year =String(index.getFullYear());
        let month =parseInt(index.getMonth())<10 ? "0"+String(parseInt(index.getMonth())+1):String(parseInt(index.getMonth())+1);
        let day =parseInt(index.getDate())<10 ? "0"+index.getDate():index.getDate();
        let time=year+month+day;
        let http='http://127.0.0.1:5000/api/saltReturns/apply/'+time;
        axios.get(http, tokenConfig(getState))
        .then((res) => {
            console.log(res.data,"000000000000000000000000000000000000000000000000");
            tempRes=tempRes.concat(res.data);
         })
        .catch(err => {
            console.log(err,"error in data");
        });
    }
    console.log("error in data",tempRes);
    store.dispatch({
        type: SALT_RETURNS,
        payload: tempRes
    });
};



// Setup config/headers and token
export const tokenConfig = getState => {
    // Get token from localstorage
    const token = getState().auth.token;
  
    // Headers
    const config = {
        headers: {
            "Content-type": "application/json"
        }
    }

    // If token, add to headers
    if(token) {
       config.headers["Authorization"] = ` Bearer ${token} `;
    }

 return config;
}
