import React, { Component } from 'react';
import { connect } from 'react-redux';
import { BrowserRouter, Route , Redirect} from 'react-router-dom';
import * as authActions from '../../actions/authActions'
import Dashboard from '../../Dashboard';
import LoginForm from '../auth/LoginForm';
import RegisterForm from '../auth/RegisterForm';
import store from '../../store';

import PropTypes from 'prop-types';

class Routes extends Component {


  static propTypes = {
    isAuthenticated: PropTypes.bool,
    error: PropTypes.object.isRequired,
    login: PropTypes.func.isRequired,
    clearErrors: PropTypes.func.isRequired
  };

  render() {
    console.log('Routes props', this.props.auth.isAuthenticated);
    console.log("roouututrututeuet");
    return (
      <BrowserRouter >
        <div>
          <Route exact path="/login" component={LoginForm} />
          <Route exact path="/register" component={RegisterForm} />
          <PrivateRoute exact path="/" component={Dashboard} authed={this.props.auth.isAuthenticated} />
        </div>
      </BrowserRouter>  

    );
  }
}

const PrivateRoute = ({ component: Component, authed, ...rest }) => (
  <Route
    {...rest}
    render={props => (
      !authed
        ? <Dashboard {...props} />
        : <Redirect to="/login" />
    )}
  />
);


const mapStateToProps = state => ({ auth: state.auth });

export default connect(mapStateToProps, authActions)(Routes);
