import { GET_ERRORS, CLEAR_ERRORS} from './types';

// Return errors
export const returnErrors = (msg, status, id = null) => {
    console.log(msg,status)
    return {
        type: GET_ERRORS,
        payload: { msg, status, id}
    };
};

// Clear errors
export const clearErrors = () => {
    return {
        type: CLEAR_ERRORS
    };
};
